/*----------------------------------------------------------------------------
Name:           LevelConverterFromBitap
------------------------------------------------------------------------------
Beschreibung: Konvertiert eine exportierte Diamond Caves III-Level-Bitmap
              in Leveldaten. Das Ergebnis wird in Ed.x abgelegt.
              Die Struktur Ed.x muss bereits teilweise initialisiert sein.

              Im Erfolgsfall alloziert die Funktion in Ed.pLevel Speicher fr
              das konvertierte Level.
Parameter
      Eingang: pszFilename, char *, Dateiname der Bitmap
      Ausgang: -
Rckgabewert:  int, 0 = Alles OK, sonst Fehler
Seiteneffekte: Ed.x, Bitmap.x
------------------------------------------------------------------------------*/
int LevelConverterFromBitap(char *pszFilename) {
    FILE *Readfile = NULL;
    char szTimestamp[16];    // Format: YYYYMMDD_HHMMSS
    struct stat Fileinfo;
    uint8_t uBlockbuf[768];
    uint32_t X,Y,I;
    uint16_t uLevelElement;
    MD5Context MD5Block;
    char szMD5String[32 + 1];   // + 1 fr Stringende
    char szNum[16];
    int nErrorCode = 0;
    bool bBottomLeftReplicator;     // Linke und rechte Seite vom unteren Replikatorteil kann nicht unterschieden werden, daher Logik

    bBottomLeftReplicator = false;
    if (pszFilename == NULL) {
        return -1;
    }
    memset(uBlockbuf,0,sizeof(uBlockbuf));
    memset(&Bitmap,0,sizeof(Bitmap));
    strcpy(Bitmap.szFilename,pszFilename);
    if (stat(Bitmap.szFilename,&Fileinfo) != 0) {
        SDL_Log("%s: file not found: %s",__FUNCTION__,Bitmap.szFilename);
        return -1;
    }
    Bitmap.uFilesize = Fileinfo.st_size;
    if  (Bitmap.uFilesize < BITMAP_MINSIZE) {
        SDL_Log("%s: bitmap to small (< 4096 Bytes), filesize: %u",__FUNCTION__,Bitmap.uFilesize);
        return -1;
    }
    Readfile = fopen(Bitmap.szFilename,"rb");
    if (Readfile == NULL) {
        SDL_Log("%s: can not open file %s",__FUNCTION__,Bitmap.szFilename);
        return -1;
    }
    if (ReadBmHeader(Readfile,&Bitmap) != 0) {
        SDL_Log("%s: invalid bitmap header, file: %s",__FUNCTION__,Bitmap.szFilename);
        return -1;
    }
    Ed.uLevel_X_Dimension = Bitmap.nW / 16;
    Ed.uLevel_Y_Dimension = Bitmap.nH / 16;
    Ed.uTmpLevel_X_Dimension = Ed.uLevel_X_Dimension;
    Ed.uTmpLevel_Y_Dimension = Ed.uLevel_Y_Dimension;

    Ed.pLevel = malloc(Ed.uLevel_X_Dimension * Ed.uLevel_Y_Dimension * sizeof(uint16_t));
    if (Ed.pLevel == NULL) {
        SDL_Log("%s: can not allocate memory for level (%u x %u)",__FUNCTION__,Ed.uLevel_X_Dimension,Ed.uLevel_Y_Dimension);
        return -1;
    }
    PrintBitmapInfos(&Bitmap);
    PrintLevelInfos(&Ed);
    for (Y = 0; (Y < Ed.uLevel_Y_Dimension) && (nErrorCode == 0); Y++) {
        for (X = 0; (X < Ed.uLevel_X_Dimension) && (nErrorCode == 0); X++) {
            nErrorCode = Read16x16Macroblock(Readfile,X,Y,uBlockbuf);
            //Show16x16Macroblock(pRenderer,0,0,uBlockbuf);
            md5Init(&MD5Block);
            md5Update(&MD5Block,uBlockbuf,768);
            md5Finalize(&MD5Block);
            szMD5String[0] = 0;
            for (I = 0; I < 16; I++) {
                sprintf(szNum,"%02X",MD5Block.digest[I]);
                strcat(szMD5String,szNum);
            }
            uLevelElement = GetElementByMD5(szMD5String);
            if (uLevelElement != EMERALD_INVALID) {
                // Der untere Teil eines Replikators kann nicht direkt unterschieden werden, daher Logik
                switch (uLevelElement) {
                    case (EMERALD_REPLICATOR_RED_BOTTOM_LEFT):
                        if (bBottomLeftReplicator) {    // es gab bereits eine linke Replikator-Hlfte
                            uLevelElement = EMERALD_REPLICATOR_RED_BOTTOM_RIGHT;
                            bBottomLeftReplicator = false;
                        } else {
                            bBottomLeftReplicator = true;
                        }
                        break;
                    case (EMERALD_REPLICATOR_YELLOW_BOTTOM_LEFT):
                        if (bBottomLeftReplicator) {    // es gab bereits eine linke Replikator-Hlfte
                            uLevelElement = EMERALD_REPLICATOR_YELLOW_BOTTOM_RIGHT;
                            bBottomLeftReplicator = false;
                        } else {
                            bBottomLeftReplicator = true;
                        }
                        break;
                    case (EMERALD_REPLICATOR_GREEN_BOTTOM_LEFT):
                        if (bBottomLeftReplicator) {    // es gab bereits eine linke Replikator-Hlfte
                            uLevelElement = EMERALD_REPLICATOR_GREEN_BOTTOM_RIGHT;
                            bBottomLeftReplicator = false;
                        } else {
                            bBottomLeftReplicator = true;
                        }
                        break;
                    case (EMERALD_REPLICATOR_BLUE_BOTTOM_LEFT):
                        if (bBottomLeftReplicator) {    // es gab bereits eine linke Replikator-Hlfte
                            uLevelElement = EMERALD_REPLICATOR_BLUE_BOTTOM_RIGHT;
                            bBottomLeftReplicator = false;
                        } else {
                            bBottomLeftReplicator = true;
                        }
                        break;
                }
                Ed.pLevel[Y * Ed.uLevel_X_Dimension + X] = uLevelElement;
            } else {
                SDL_Log("%s: Warning: hash (%s) not found for element at position X:%u  Y:%u",__FUNCTION__,szMD5String,X,Y);
                //WriteFile("hashes.txt",szMD5String,32,true);
                //WriteFile("hashes.txt","\r\n",2,true);

                //Ed.pLevel[Y * Ed.uLevel_X_Dimension + X] = EMERALD_SPACE;
                Ed.pLevel[Y * Ed.uLevel_X_Dimension + X] = EMERALD_FONT_STEEL_QUESTION_MARK;
            }
            //SDL_RenderPresent(pRenderer);   // Renderer anzeigen, lsst Hauptschleife mit ~ 60 Hz (Bild-Wiederholfrequenz) laufen
            //SDL_RenderClear(pRenderer);     // Renderer fr nchstes Frame lschen
        }
    }
    if (nErrorCode == 0) {
        GetActualTimestamp(szTimestamp);    // Format: YYYYMMDD_HHMMSS
        strcpy(Ed.szLevelTitle,"IMPORT DC3 ");
        strcat(Ed.szLevelTitle,szTimestamp);
        strcpy(Ed.szLevelAuthor,"LEVELIMPORTER");
        Ed.uTimeToPlay = 100;
        Ed.uEmeraldsToCollect = 50;
        Ed.uTimeScoreFactor = 40;
    } else {
        SAFE_FREE(Ed.pLevel);
    }
    fclose(Readfile);
    return nErrorCode;
}


/*----------------------------------------------------------------------------
Name:           Show16x16Macroblock
------------------------------------------------------------------------------
Beschreibung: Schreibt einen 16x16-Pixelblock in den Renderer.

Parameter
      Eingang: pRenderer, SDL_Renderer *, Zeiger auf Renderer
               uXpos, uint32_t, X-Position fr Block
               uYpos, uint32_t, Y-Position fr Block
               puBlockbuf, uint8_t *, Zeiger auf RGB-Daten des 16x16-Makroblocks
      Ausgang: -
Rckgabewert:  0 = alles OK, sonst Fehler
Seiteneffekte: -
------------------------------------------------------------------------------*/
int Show16x16Macroblock(SDL_Renderer *pRenderer,uint32_t uXpos,uint32_t uYpos,uint8_t *puBlockbuf) {
    uint32_t uLine;
    uint32_t uPoint;
    uint32_t uOff;
    int nErrorCode;

    nErrorCode = 0;
    for (uLine = 0; (uLine <= 15) && (nErrorCode == 0); uLine++) {
        for (uPoint = 0; (uPoint <= 15) && (nErrorCode == 0); uPoint++) {
            uOff = (uLine * 16 * 3) + (uPoint * 3);
            // Set our color for the draw functions
            nErrorCode = SDL_SetRenderDrawColor(pRenderer, puBlockbuf[uOff + 2], puBlockbuf[uOff + 1], puBlockbuf[uOff + 0], 0xFF);
            // Now we can draw our point
            nErrorCode = SDL_RenderDrawPoint(pRenderer, uXpos + uPoint, uYpos + uLine);
        }
    }
    // Set the color to what was before
    SDL_SetRenderDrawColor(pRenderer, 0x00, 0x00, 0x00, 0xFF);
    return nErrorCode;
}


/*----------------------------------------------------------------------------
Name:           ReadBmHeader
------------------------------------------------------------------------------
Beschreibung: Liest einen Bitmap-Header (14 Bytes) und Bitmap-Info-Header (40 Bytes) ein.

Parameter
      Eingang: pF, FILE *, gltiges Lese-Handle auf Bitmap-Datei
               pBitmap, BITMAP *, Zeiger auf Bitmap-Struktur
      Ausgang: pBitmap, BITMAP *, Eingelesener Bitmap-Header
Rckgabewert:  0 = alles OK, sonst Fehler
Seiteneffekte: -
------------------------------------------------------------------------------*/
int ReadBmHeader(FILE *pF,BITMAP *pBitmap) {
    int nErrorCode = -1;
    uint32_t uInfoHeaderSize;
    uint16_t uColorBitsPerPixel;
    uint32_t uCompressionMode;

    if (fread(pBitmap->cHeader,1,14,pF) == 14) { // Header lesen
        if ((pBitmap->cHeader[0] == 'B') && (pBitmap->cHeader[1] == 'M')) {
            pBitmap->uDataOffset = *(uint32_t *)(pBitmap->cHeader + 10);
            if (fread(pBitmap->cInfoHeader,1,40,pF) == 40) { // Info-Header lesen
                uInfoHeaderSize = *(int32_t *)(pBitmap->cInfoHeader + 0);
                pBitmap->nW = *(int32_t *)(pBitmap->cInfoHeader + 4);
                pBitmap->nH = *(int32_t *)(pBitmap->cInfoHeader + 8);

                pBitmap->bBottomUp = (pBitmap->nH > 0);
                if (!pBitmap->bBottomUp) {
                    pBitmap->nH = -pBitmap->nH;
                }
                uColorBitsPerPixel = *(int16_t *)(pBitmap->cInfoHeader + 14);
                uCompressionMode = *(uint32_t *)(pBitmap->cInfoHeader + 16);
                if (uInfoHeaderSize == 40) {
                    if ((uColorBitsPerPixel == 24) || (uColorBitsPerPixel == 32)) {
                        pBitmap->uBytesPerPixel = uColorBitsPerPixel / 8;
                        if (uCompressionMode == 0) {
                            pBitmap->uBytesPerLine = pBitmap->uBytesPerPixel * pBitmap->nW;
                            nErrorCode = 0;
                        } else {
                            printf("unsupported compression mode: %u\n",uCompressionMode);
                        }
                    } else {
                        printf("unsupported colordepth: %u bits/pixel\n",uColorBitsPerPixel);
                    }
                } else {
                    printf("bad info header size: %u\n",uInfoHeaderSize);
                }
            }
        } else {
            printf("no 'BM' found\n");
        }
    }
    return nErrorCode;
}


