/*----------------------------------------------------------------------------
Name:           PrintLevelInfos
------------------------------------------------------------------------------
Beschreibung: Gibt Elemente der ED-Struktur aus.
Parameter
      Eingang: pF, FILE *, gltiges Lese-Handle auf Bitmap-Datei
               X, uint32_t, Levelposition X
               Y, uint32_t, Levelposition Y
               puBlockbuf, uint8_t *, Zeiger auf Speicher 16 x 16 x 3 = 768 Bytes
      Ausgang: -
Rckgabewert:  -
Seiteneffekte: Bitmap.x, Ed.x
------------------------------------------------------------------------------*/
int Read16x16Macroblock(FILE *pF, uint32_t X, uint32_t Y,uint8_t *puBlockbuf) {
    long Offset = 0;
    int nErrorCode;
    uint32_t uLine;
    uint32_t L;
    uint32_t P;
    uint32_t uBlockPtr;
    uint8_t uTmpBuf[8];

    uBlockPtr = 0;
    nErrorCode = 0;
    if ((X < Ed.uLevel_X_Dimension) && (Y <Ed.uLevel_Y_Dimension)) {
        if (Bitmap.bBottomUp) {
            uLine = (Bitmap.nH - 1) - Y * 16;
        } else {
            uLine = Y * 16;
        }
        for (L = 0; (L <= 15) && (nErrorCode == 0); L++) {
            Offset = Bitmap.uDataOffset + uLine * Bitmap.uBytesPerLine + X * 16 * Bitmap.uBytesPerPixel;
            nErrorCode = fseek(pF,Offset,SEEK_SET); // Gibt 0 bei Erfolg zurck
            if (Bitmap.uBytesPerPixel == 3) {
                if (fread(puBlockbuf + uBlockPtr,1,16 * 3,pF) == 16 * 3) {
                    uBlockPtr = uBlockPtr + (16 * 3);
                } else {
                    nErrorCode = -1;
                }
            } else if (Bitmap.uBytesPerPixel == 4) {
                for (P = 0; (P <= 15) && (nErrorCode == 0); P++) {
                    if (fread(uTmpBuf,1,4,pF) == 4) {
                        memcpy(puBlockbuf + uBlockPtr,uTmpBuf,3);
                        uBlockPtr = uBlockPtr + 3;
                    } else {
                        nErrorCode = -1;
                    }
                }
            } else {
                nErrorCode = -1;
            }

            if (Bitmap.bBottomUp) { // Von unten nach oben
                uLine--;
            } else {
                uLine++;            // Von oben nach unten
            }
        }
    } else {
        printf("bad block, x = %u,  y = %u\n",X,Y);
    }
    return nErrorCode;
}


/*----------------------------------------------------------------------------
Name:           GetElementByMD5
------------------------------------------------------------------------------
Beschreibung: Ermittelt anhand eines MD5-Hashes ein zugehriges Levelelement.
Parameter
      Eingang: szMd5, char *, Zeiger auf MD5-Hash
      Ausgang: -
Rckgabewert:  uint16_t, Level-Element, EMERALD_INVALID, wenn Hash nicht gefunden wurde
Seiteneffekte: g_szDC3_MD5[], g_DC3_Elements[]
------------------------------------------------------------------------------*/
uint16_t GetElementByMD5(char *szMd5) {
    bool bRun;
    uint16_t uElement;
    uint32_t I;

    uElement = EMERALD_INVALID;
    if (szMd5 != NULL) {
        I = 0;
        do {
            bRun = (strcmp(g_szDC3_MD5[I],"FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF") != 0);
            if (bRun) {
                if (strcmp(g_szDC3_MD5[I],szMd5) == 0) {
                    uElement = g_DC3_Elements[I];
                    bRun = false;
                }
            }
            I++;
        } while (bRun);
    }
    return uElement;
}


